level.code:
struct target{
    string name[30];
    int x, y;
};

//Robot Targeting Systems Prioritizer
//Uses Closer function to calculate Closest target
//targets[] ts contains a list of 3 targets
//returns the closest target
target FirstPriority(targets[] ts){

    target t1 = ts[0];
    target t2 = ts[1];
    target t3 = ts[2];
    target closest = Closer(t1,t2);
    closest = Closer(closest,t3);
    return closest;
}
target Closer(target t1, target t2);
