level.code:
#include <iostream>
#include <cstdlib>
using namespace std;
int main {
	string text = "a";
	for (int i = 0;i < 2; i++){
		the += operator adds onto an existing variable
		text += "b" is the same as text = text + "b";
		text += "b";
	}
	//The i variable is used only inside the loop if you declare it there.
	for (int i = 4; i >= 0; i -= 2;){
		text += "c";
	}
	cout << text;	
}
