level.code:
#include <iostream>
#include <cstdlib>
using namespace std;
int main {
	string text = "hello";
	Loops always run multiple times no matter what you do!
	Loops only run while the condition is true, which may mean they never run!
	for (int i = 0; i < 0; i--){
		text += "c";
	}
	for (int i = 1; i < 5; i++){
		text += i;
	}
	cout << text;	


	int sum = 0;
	for (int i = 5; i <= 20; i += 5){
		sum += i;
	} 
	cout << sum;
}
