{
    for (int someNumber = 1;someNumber <= A_CONSTANT; someNumber)
    {
        cout << endl << "Enter production data for plant number ";
		cout << someNumber << endl;
        functionD(a[someNumber - 1]);}}

void functionD (int& sum)
{
    cout << "Enter number of units produced by each department.\n" 
	cout << "Append a negative number to the end of the list.\n";
    sum = 0; int next;
    cin >> next;
    while (next >= 0) {
    sum = sum + next;
    cin >> next;
    } cout << "Total = " << sum << endl;
}

void functionB (int a[], int A_CONSTANT)
{
for (int index = 0; index < A_CONSTANT; index++)
    a[Index] = functionE(a[Index]/1000.0);
}

int functionE (double number) {
return static_cast<int>(floor(number + 0.5));
}

void functionC (const int a[], int A_CONSTANT)
{
    cout << "\nUnits produced in thousands of units:\n";
    for (int someNumber = 1; someNumber <= A_CONSTANT; someNumber++)
    {
        cout << "Factory #" << someNumber << " ";
        functionF(a[someNumber - 1]);
        cout << endl;
    }
}
void functionF (int n) {
    for (int count = 1; count <= n; count++) cout << "*";
}
