	def approx_arclength(tn,m,xp,yp):
    Al = 0.0
    deltaM = tn/m
    for j in range(m):
        deltaX = xp((j+1)*deltaM) - xp(j*deltaM)
        deltaY = yp((j+1)*deltaM) - yp(j*deltaM)

        Al += math.sqrt(deltaX**2 + deltaY**2)
    return Al
}
