level.code:
#include <iostream>
#include <cstdlib>
using namespace std;
int main {
	int i = 0;
	//code in an IF statement is only run IF the condition is true
	if (2 > 1){
		i++; //increase i by 1
	}
	//when the condition is false, code in an ELSE block runs instead
	if (10 < 5){
		i++;
	}
	else{
		i--; //reduce i by 1
	}
	//when you want multiple conditions, you can use ELSE IF
	if (25 < 20){
		i = 0;	
	}
	else if (25 < 30){
		i++;
	}
	else{
		i--;
	}
	//which of these will be true?
	//bool correct = i == 0;
	//bool correct = i == 1;
}
