#include <iostream>
#include <cstdlib>
using namespace std;
int main {
	WHILE loops run as long as the condition is true
	int i = 0;
	while (i < 3){
		//activate the beacon once for each iteration!
		cout << "This loop has run " << i << " times!" << endl;
		i++;
	}
	FOR loops run for a fixed number of iterations.
	//They consist of three parts:
	Initialization,
	Condition, 
	and the Change per iteration

	//This loop will be identical to the previous one!
	for (int j = 0; j < 3; j++){
				cout << "This loop has run " << j << " times!" << endl;
	}
}
